import React from 'react'
import Router from 'next/router'
// import '../styles/base.styl'
// import '../styles/header.styl'
import './header.css'
// import grid from '../../styles/grid.css'



export default class Header extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            menus: [
                { name: '首页', path: '/', component: 'index' },
                { name: '赛事', path: '/race', component: 'race' },
                { name: '证书', path: '/certificate', component: 'certificate' },
                { name: '关于我们', path: '/aboutus', component: 'aboutus' }
            ],
            currentPath: ''
        }
    }
    componentDidMount() {
        this.setState({
            currentPath: Router.router.route || '/'
        })
    }
    render() {
        const { menus, currentPath } = this.state
        return (
            <div id={header.header}>
                <div className={header.content}>
                    <div className={header.menus}>
                        <img src="/static/images/icode.png" className='mr-20' alt="" />
                        {
                            menus.map((v, k) => <div className={currentPath === v.path ? `ml-50 ${header.selected}` : 'ml-50'} key={k} onClick={() => Router.push({ pathname: v.path || '/' }, v.component)}>{v.name}</div>)
                        }
                    </div>
                    <div className='entrys'>
                        <button className={header.title}>登录</button>
                        <button>学校/机构报名</button>
                    </div>
                </div>
            </div>
        )
    }
}
