import Head from 'next/head'
import React from 'react'
import Router from 'next/router'
import '../styles/base.styl'
import '../styles/header.styl'

class Header extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      menus: [
        { name: '首页', path: '/' },
        { name: '赛事', path: '/race' },
        { name: '证书', path: '/certificate' },
        { name: '关于我们', path: '/aboutus' }
      ],
      currentPath: ''
    }
  }
  componentDidMount() {
    this.setState({
      currentPath: Router.router.route || '/'
    }, () => {
      console.log(this.state.currentPath)
    })
  }
  render() {
    const { menus, currentPath } = this.state
    return <div>
      <Head>
        <title>ICode国际青少年编程竞赛</title>
        <link rel="icon" type="image/x-icon" href="/static/favicon.ico" />
        <meta name="viewport" content="initial-scale=1.0, width=device-width" />
        <body>
          <div id="header">
            <div className='wd-per-100 text-center content flex items-center flex-between pl-30 pr-30'>
              <div className='menus hgh-per-100 flex items-center'>
                <img src="/static/images/icode.png" className='mr-20' alt="" />
                {
                  menus.map((v, k) => <div className={currentPath === v.path ? 'ml-50 selected' : 'ml-50'} key={k} onClick={() => Router.push(v.path || '/')}>{v.name}</div>)
                }
              </div>
              <div className='entrys'>
                <button>登录</button>
                <button>学校/机构报名</button>
              </div>
            </div>
          </div>
        </body>
      </Head>
    </div>
  }
}
export default Header
