import App from 'next/app'
import Head from 'next/head'
import Router from 'next/router'
import React from 'react'
// import '../styles/base.styl'
// import '../styles/grid.styl'
import '../styles/common.styl'

const header = {
    boxShadow: '0 2px 2px #ccc'
}


export default class MyApp extends App {
    static async getInitialProps({ Component, ctx }) {
        let pageProps = {}

        if (Component.getInitialProps) {
            pageProps = await Component.getInitialProps(ctx)
        }

        return { pageProps }
    }

    constructor(props) {
        super(props)
        this.state = {
            menus: [
                { name: '首页', path: '/', component: 'index' },
                { name: '赛事', path: '/race', component: 'race' },
                { name: '证书', path: '/certificate', component: 'certificate' },
                { name: '关于我们', path: '/aboutus', component: 'aboutus' }
            ],
            currentPath: ''
        }
    }

    render() {
        const { Component, pageProps } = this.props
        const { currentPath, menus } = this.state
        return (
            <>
                <Head>
                    <title>ICode国际青少年编程竞赛</title>
                </Head>
                <div style={header}>
                    <div className='wd-per-100 flex items-center flex-between'>
                        <div className='menus'>
                            <img src="/static/images/icode.png" className='mr-20' alt="" />
                            {
                                menus.map((v, k) => <div className={currentPath === v.path ? `ml-50  selected` : 'ml-50'} key={k} onClick={() => Router.push({ pathname: v.path || '/' }, v.component)}>{v.name}</div>)
                            }
                        </div>
                        <div className='entrys'>
                            <button className='title'>登录</button>
                            <button>学校/机构报名</button>
                        </div>
                    </div>
                </div>
                <Component {...pageProps} />
            </>
        )
    }
}
