import React from 'react'
import Router from 'next/router'
import '../styles/base.styl'
import '../styles/header.styl'


export default class Header extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            menus: [
                { name: '首页', path: '/index', component: 'index' },
                { name: '赛事', path: '/race', component: 'race' },
                { name: '证书', path: '/certificate', component: 'certificate' },
                { name: '关于我们', path: '/aboutus', component: 'aboutus' }
            ]
        }
    }
    render() {
        const { menus } = this.state
        const { currentPath } = this.props
        return (
            <div id='header'>
                <div className='wd-per-100 text-center content flex items-center flex-between pl-30 pr-30'>
                    <div className='menus hgh-per-100 flex items-center'>
                        <img src="/static/images/icode.png" className='mr-20' alt="" />
                        {
                            menus.map((v, k) => <div className={currentPath === v.path ? 'ml-50 selected' : 'ml-50'} key={k} onClick={() => Router.push({ pathname: v.path || '/' }, v.component)}>{v.name}</div>)
                        }
                    </div>
                    <div className='entrys'>
                        <button>登录</button>
                        <button>学校/机构报名</button>
                    </div>
                </div>
            </div>
        )
    }
}
