import Head from 'next/head'
import React from 'react'
import Router from 'next/router'
import '../styles/base.styl'
import '../styles/header.styl'

class Header extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      menus: [
        { name: '首页', path: '/', component: 'index' },
        { name: '赛事', path: '/race', component: 'race' },
        { name: '证书', path: '/certificate', component: 'certificate' },
        { name: '关于我们', path: '/aboutus', component: 'aboutus' }
      ],
      currentPath: ''
    }
  }
  componentDidMount() {
    this.setState({
      currentPath: Router.router.route || '/'
    }, () => {
      // console.log(this.state.currentPath)
    })
  }
  render() {
    const { menus, currentPath } = this.state
    return <div id='header'>
      <Head>
        <title>ICode国际青少年编程竞赛</title>
        <link rel="icon" type="image/x-icon" href="/static/favicon.ico" />
        <meta name="viewport" content="initial-scale=1.0, width=device-width" />
      </Head>
    </div>
  }
}
export default Header
